import UIKit
import SnapKit

protocol OGSChooseGameViewControllerInput
{
    func displayListGames(viewModel: OGSChooseGame.ListGames.ViewModel)
}

protocol OGSChooseGameViewControllerOutput
{
    func listGames(request: OGSChooseGame.ListGames.Request)
}

class OGSChooseGameViewController: UIViewController, OGSChooseGameViewControllerInput
{
    var output: OGSChooseGameViewControllerOutput!
    var router: OGSChooseGameRouter!
    var collectionViewController: OGSChooseGameCollectionViewController!

    override func awakeFromNib()
    {
        super.awakeFromNib()
        OGSChooseGameConfigurator.sharedInstance.configure(viewController: self)
    }

    override func viewDidLoad()
    {
        super.viewDidLoad()
        setupViews()
        listGamesOnLoad()
    }
}

// MARK: - Event Handling
fileprivate extension OGSChooseGameViewController
{
    func listGamesOnLoad()
    {
        let request = OGSChooseGame.ListGames.Request()

        output.listGames(request: request)
    }
}

// MARK: - Display Logic
extension OGSChooseGameViewController
{
    func displayListGames(viewModel: OGSChooseGame.ListGames.ViewModel)
    {
    }
}

// MARK: - View Setup
extension OGSChooseGameViewController {
    func setupViews() {
        setupCollectionView()
    }

    func setupCollectionView()
    {
        collectionViewController = OGSChooseGameCollectionViewController()
        collectionViewController.automaticallyAdjustsScrollViewInsets = true
        addChildViewController(collectionViewController)
        view.addSubview(collectionViewController.view)

        collectionViewController.collectionView?.snp.makeConstraints { make in
            make.edges.equalToSuperview()
        }
    }
}